import * as React from 'react';
import { Text, View,StyleSheet } from 'react-native';
import { NavigationContainer } from '@react-navigation/native';
import { createBottomTabNavigator } from '@react-navigation/bottom-tabs';
import Ionicons from '@expo/vector-icons/Ionicons';
import Home from './screens/Home'
import Post from './screens/Post'
import Account from './screens/Account'

const Tab = createBottomTabNavigator();

export default function App() {
  return (
    <NavigationContainer>
      <Tab.Navigator
        screenOptions={({ route }) => ({
          tabBarIcon: ({ focused, color, size }) => {
            let iconName;
            //console.log(color,size);
            if (route.name === 'Home') {
              iconName = focused
                ? 'home'
                : 'home-outline';
            } else if (route.name === 'Account') {
              iconName = focused ? 'person-circle' : 'person-circle-outline';
            }else if (route.name === 'Post'){
              iconName = focused ? 'add-circle' : 'add-circle-outline';
            }
            // You can return any component that you like here!
            return <Ionicons name={iconName} size={size} color={color} />;
          },
          tabBarActiveTintColor: 'tomato',
          tabBarInactiveTintColor: 'gray',
        })}
      >
        <Tab.Screen name="Home" component={Home} options={{ title: '' , headerShown: false,
    headerTransparent: true, 
        }}/>
        <Tab.Screen name="Post" component={Post} options={{ title: '' , headerShown: false,
    headerTransparent: true, 
        }}/>
        <Tab.Screen name="Account" component={Account} options={{ title: '' , headerShown: false,
    headerTransparent: true, 
        }}/>
      </Tab.Navigator>
    </NavigationContainer>
  );
}
const styles = StyleSheet.create({
  textStyle: {
    fontSize: 40,
  }
})
