import * as React from 'react';
import { Text, View,StyleSheet, FlatList } from 'react-native';
import PostCard from './PostCard'
function Post(){
    return (    
        <FlatList style={styles.mainView}>
        <PostCard></PostCard>
        </FlatList>
    );
}
const styles = StyleSheet.create({
    mainView:{
        flex: 1, 
        justifyContent: 'center', 
        alignItems: 'center'
    }
})
export default Post;
