import * as React from 'react';
import { Text,ScrollView, SafeAreaView ,StyleSheet, FlatList } from 'react-native';
import PostCard from './PostCard'
function Post(){
    let arr = [1,2,3];
    return (
        <SafeAreaView  style={styles.mainView}>
        <FlatList
        style={styles.list}
        data={arr}
        renderItem={({item})=>(
        <PostCard></PostCard>
        )}
        keyExtractor={(item)=> item}
        />
        </SafeAreaView>
    );
}
const styles = StyleSheet.create({
    mainView:{
        flex: 1, 
        justifyContent: 'center', 
        alignItems: 'center'
    },
    list:{
        width: '100%',
        height: '100%'
    }
})
export default Post;
